import React from 'react'

const ChangePassword = () => {

    const [username, setUsername] = useState("");
    const [Password, setPassword] = useState("");


    function handleChangePassword() {
        console.log('userName', username, 'password', Password)
        // alert(`username: ${userName} Password : ${password}`);

        axios.post('http://localhost:3000/login', {

            username: username,
            password: Password

        })
            .then(res => {
                alert(res.data.message)
                console.log(res);
                window.location.href = "/Home";

            })
            .catch(error => {
                alert(error.response.data.message)
                console.log(error);
            }
            )
    };

    return (
        <div className='fv-const'>
            <div className='lq-maion'>
                <h1>Đổi Mật Khẩu</h1>
                <h5 className='gh-kist'>Nhập Họ và Tên</h5>
                <input value={username} onChange={(a) => { setUsername(a.target.value) }} type="text" placeholder="Nhập Họ và Tên" className='dw-vosi' />
                <h5 className='gh-kist'>Nhập  mật khẩu cũ</h5>
                <input  value={Password} onChange={(a) => { setPassword(a.target.value) }} type="text" placeholder="Nhập lại mật khẩu cũ" className='dw-vosi' />
                <h5 className='gh-kist'>Nhập mật khẩu mới</h5>
                <input type="text" placeholder="Nhập mật khẩu mới " className='dw-vosi' />
                <h5 className='gh-kist'>Nhập lại mật khẩu </h5>
                <input type="text" placeholder="Nhập lại mật khẩu" className='dw-vosi' />
                
                <h1></h1>
                <div className='rt-mast'>
                    <button onClick={handleChangePassword} type='text'>Lưu thay đổi</button>
                </div>

            </div>

        </div>
    )
}

export default ChangePassword
