import React, { useState } from 'react'
import axios from 'axios';

const  ChangePassword = () => {

    const [username, setUsername] = useState("");
    const [Password, setPassword] = useState("");
    const [newPassword, setnewPassword] = useState("");
    const [enterpassword, setenterpassword] = useState("");

    function handlelogin() {
        console.log('userName', username, 'password', Password ,'newPassword',newPassword)
        // alert(`username: ${userName} Password : ${password}`);

        axios.update('http://localhost:3000/login', {

            username: username,
            password: Password,
            newPassword:newPassword

        })
            .then(res => {
                alert(res.data.message)
                console.log(res);
                window.location.href = "/Home";

            })
            .catch(error => {
                alert(error.response.data.message)
                console.log(error);
            }
            )
    };
    return (
        <div className='cb-body'> 
        <div className='cd-body'> 
        <div className='lq-maion'>
             
             <div className='dw-vosi'> 
              <h1 className='bg-main'>Đổi mật khẩu</h1>
            <input value={username} onChange={(a) => { setUsername(a.target.value) }} type="text" placeholder="Nhap username"  className='cd-input'/>
            <h1></h1>
            <input value={Password} onChange={(a) => { setPassword(a.target.value) }} type="text" placeholder="Nhap password" className='cd-input' />
            <h1></h1>
            <input value={newPassword} onChange={(a) => { setnewPassword(a.target.value) }} type="text" placeholder="Nhap mat khau moi"  className='cd-input'/>
            <h1></h1>
            <input value={enterpassword} onChange={(a) => { setenterpassword(a.target.value) }} type="text" placeholder="Nhap mat khau moi"  className='cd-input'/>
                <button type="button" onClick={handlelogin} className='bf-mitring'>Xác nhận</button>   
            </div>   
        </div>
        </div>
        </div>
    );
}
export default ChangePassword


























































