import React, { useState } from 'react'
import Sile from '../assets/images/Sile.jpg'
import Homepage from '../component/Home';
import Home from '../component/Home'
import { render } from 'react-dom';
// import axios from 'axios';
const Loginpage = () => {


    const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");

    axios.post(`http://localhost:3000/login`, { username:'minhtv' , password: '9999' })
    .then(res => {
        if (res.data.lo== false) {
            alert(res.data.message);
        } else {
            console.log(res)
            window.location.href = "/Home";
        }
    })
    .catch(error => 
        console.log(error));

    function handlelogin() {
  
        alert(`username: ${userName} Password : ${password}`);


         

    };



    return (
        <div className='contenner'>
            <div className='host'>
                <img src={Sile} width='750px' height='750px' />
            </div>
            <div className='lest'>
                <div className='cover'>
                    <div className='cd-h2'>
                        <h2> LOGIN</h2>
                    </div>
                    <input value={userName} onChange={(a) => { setUserName(a.target.value) }} type="text" placeholder="nhập username" />
                    <h1></h1>
                    <input value={password} onChange={(a) => { setPassword(a.target.value) }} type="text" placeholder="nhập password" />
                    <h1></h1>
                    <div className='hh'>
                        <button type="button" onClick={handlelogin}>Đăng nhập</button>
                    </div>
                    <div>
                        <h1></h1>
                        <a href="https://www.google.com/search?q=%C4%91%E1%BB%95i+mk+wifi&sxsrf=AJOqlzVjWTD09IiIlk-OwDvmap7ZSuzLBA:1675767521172&source=lnms&tbm=isch&sa=X&ved=2ahUKEwjT-My-oIP9AhXEUGwGHdfCDgoQ_AUoAnoECAMQBA&biw=1536&bih=746&dpr=1.25#imgrc=4k4z6rm6jHHlbM">Quên mật khẩu...?</a>
                    </div>
                </div>


            </div>
        </div>
    );
}
export default Loginpage
