import React from 'react'
import Sile from '../assets/images/img Sile.jpg'
import Homepage from '../component/Home';
import Home from '../component/Home'
const Loginpage = () => {

    function handlelogin(status) {
        const username = 'dasdas'
        console.log('username:', username)

    };
    class NameForm extends React.Component {
        constructor(props) {
            this.state = { value: '' };
            this.handlelogin = this.handleChan.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
        }

        handlelogin(event) {
            this.setState({ value: event.target.value });
        }

        handleSubmit(event) {
            console('Bạn đã nhập tên hoặc : ' + this.state.value);
            event.preventDefault();
        }

        render() {
            return (
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Name:
                        <input type="text" value={this.state.value} onLogin={this.handlelogin} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            );
        }
    }

    return (
        <div className='contenner'>
            <div className='host'>
                <img src={Sile} width='750px' height='550px' />
            </div>
            <div className='lest'>
                <div className='cover'>
                    <div className='cd-h2'>
                        <h2> LOGIN</h2>
                    </div>
                    <input id='minhta' type="text" placeholder="nhập username" />
                    <h1></h1>
                    <input id='minhta123' type="text" placeholder="nhập password" />
                    <h1></h1>
                    <div className='hh'>
                        <button type="button" onClick={handlelogin}>Đăng nhập</button>
                    </div>
                    <div>
                        <h1></h1>
                        <a href="https://www.google.com/search?q=%C4%91%E1%BB%95i+mk+wifi&sxsrf=AJOqlzVjWTD09IiIlk-OwDvmap7ZSuzLBA:1675767521172&source=lnms&tbm=isch&sa=X&ved=2ahUKEwjT-My-oIP9AhXEUGwGHdfCDgoQ_AUoAnoECAMQBA&biw=1536&bih=746&dpr=1.25#imgrc=4k4z6rm6jHHlbM">Quên mật khẩu...?</a>
                    </div>
                </div>


            </div>
        </div>
    );
}
export default Loginpage
