import React, { useState } from 'react'
import Sile from '../assets/images/mjksd.png'
import axios from 'axios';

const Loginpage = () => {

    const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");


    function handlelogin() {
        console.log('userName', userName, 'password', password)
        // alert(`username: ${userName} Password : ${password}`);

        axios.post('http://localhost:3000/login', {

            username: userName,
            password: password

        })
            .then(res => {
                alert(res.data.message)
                console.log(res);
                window.location.href = "/Home";

            })
            .catch(error => {
                alert(error.response.data.message)
                console.log(error);
            }
            )
    };

    return (
        
        <div className='cover'>
            <div className='cd-h2'>
                <h2>Chào mừng bạn đến với <br></br> SHub Classroom!</h2>
            </div>
            <input value={userName} onChange={(a) => { setUserName(a.target.value) }} type="text" placeholder="nhập username" />
            <h1></h1>
            <input value={password} onChange={(a) => { setPassword(a.target.value) }} type="text" placeholder="nhập password" />
            <h1></h1>
            <div className='hh'>
                <button type="button" onClick={handlelogin}>Đăng nhập</button>
            </div>
            <div>
                <h1></h1>
                <a href="">Quên mật khẩu...?</a>
            </div>
            <div className='cd-lest'>
                <img src={Sile} width="850px" height="600px"></img>
            </div>
        </div>
   
    );
}



export default Loginpage
